import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const StockDetails = () => {
  const { companyName } = useParams(); // Get the company name from URL parameters
  const [stockDetails, setStockDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockDetails = async () => {
      try {
        // Use backticks and encodeURIComponent to form the URL
        const response = await fetch(`http://localhost:5000/api/stocks/company/${encodeURIComponent(companyName)}`);
        
        if (!response.ok) {
          throw new Error(`Error fetching stock details: ${response.statusText}`);
        }

        const data = await response.json();
        setStockDetails(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching stock details:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchStockDetails();
  }, [companyName]);

  if (loading) {
    return <div>Loading stock details...</div>;
  }

  if (error) {
    return <div>Error fetching stock details: {error}</div>;
  }

  return (
    <div>
      {/* Include the Navbar */}
      <Navbar />

      {/* Stock Details Container */}
      <div className="stock-details-container">
        <h1 className="company-name">{stock.company_name || 'Company Name'}</h1>

        {/* Price Summary Section */}
        <div className="summary-container">
          <div className="price-summary box">
            <h3>Price summary</h3>
            <p>TODAY'S HIGH: ₹{stock.today_high || 'N/A'}</p>
            <p>TODAY'S LOW: ₹{stock.today_low || 'N/A'}</p>
            <p>52 Week High: ₹{stock.high_52week || 'N/A'}</p>
            <p>52 Week LOW: ₹{stock.low_52week || 'N/A'}</p>
          </div>

          {/* Buy/Sell Recommendations Section */}
          <div className="recommendations box">
            <h3>Buy/Sell Recommendations</h3>
            <p>Current Price: ₹{stock.current_price || 'N/A'}</p>
            <p>Mean Price Target: ₹{stock.mean_price_target || 'N/A'}</p>
            <p>High Price Target: ₹{stock.high_price_target || 'N/A'}</p>
            <p>Low Price Target: ₹{stock.low_price_target || 'N/A'}</p>
          </div>

          {/* Company Essentials Section */}
          <div className="company-essentials box">
            <h3>Company Essentials</h3>
            <p>Market Cap: ₹{stock.market_cap || 'N/A'}</p>
            <p>No. of Shares: {stock.no_of_shares || 'N/A'} Cr</p>
            <p>Sales Growth: {stock.sales_growth || 'N/A'}%</p>
            <p>ROE: {stock.roe || 'N/A'}%</p>
            <p>ROCE: {stock.roce || 'N/A'}%</p>
            <p>Profit Growth: {stock.profit_growth || 'N/A'}%</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StockDetails;
