import React, { useEffect, useState } from 'react';
import './StockTable.css';

const StockTable = () => {
  const [stocks, setStocks] = useState([]);
  const [filteredStocks, setFilteredStocks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then((response) => response.json())
      .then((data) => {
        setStocks(data);
        setFilteredStocks(data); // Initially, filtered stocks are the same as all stocks
      })
      .catch((error) => console.error('Error fetching stock data:', error));
  }, []);

  // Handle search input
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    const filtered = stocks.filter((stock) =>
      stock.company.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setFilteredStocks(filtered);
  };

  return (
    <div className="stock-table-container">
      {/* Filter bar */}
      <div className="filter-bar">
        <input
          type="text"
          placeholder="Search by company name..."
          value={searchTerm}
          onChange={handleSearch}
          className="search-input"
        />
      </div>

      <table className="stock-table">
        <thead>
          <tr>
            <th>COMPANY</th>
            <th>YESTERDAY OPENING</th>
            <th>YESTERDAY CLOSING</th>
            <th>TODAY HIGH</th>
            <th>TODAY LOW</th>
            <th>CURRENT VALUE</th>
          </tr>
        </thead>
        <tbody>
          {filteredStocks.map((stock, index) => (
            <tr key={index}>
              <td>{stock.company}</td>
              <td>{stock.yesterday_opening}</td>
              <td>{stock.yesterday_closing}</td>
              <td>{stock.today_high}</td>
              <td>{stock.today_low}</td>
              <td>{stock.current_value}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockTable;
