import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './Navbar';
import StockTable from './components/StockTable'; // Ensure this is your existing StockTable component
import Home from './Home'; // Import your Home component
import './App.css'; // Your main CSS file

const App = () => {
  const [stocks, setStocks] = useState([]);
  const [filteredStocks, setFilteredStocks] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then((response) => response.json())
      .then((data) => {
        setStocks(data);
        setFilteredStocks(data); // Initialize filtered stocks
      })
      .catch((error) => console.error('Error fetching stock data:', error));
  }, []);

  const handleSearch = (searchTerm) => {
    const filtered = searchTerm
      ? stocks.filter(stock => 
          stock.Company && stock.Company.toLowerCase().includes(searchTerm.toLowerCase())
        )
      : stocks; // Show all stocks if the search term is empty

    setFilteredStocks(filtered);
  };

  return (
    <Router>
      <Navbar onSearch={handleSearch} />
      <Routes>
        <Route path="/" element={<Home />} /> {/* Replace with your actual Home component */}
        <Route path="/stocks" element={<StockTable stocks={filteredStocks} />} /> {/* Pass filtered stocks to StockTable */}
      </Routes>
    </Router>
  );
};

export default App;
