import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../Navbar';
import '../components/StockDetails.css';

const StockDetails = () => {
  const { companyName } = useParams();
  const [stockDetails, setStockDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/stocks/company/${encodeURIComponent(companyName)}`);

        if (!response.ok) {
          throw new Error(`Error fetching stock details: ${response.statusText}`);
        }

        const data = await response.json();

        if (data && data.company_name) {
          setStockDetails(data);
        } else {
          throw new Error('Company name not found in data');
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching stock details:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchStockDetails();
  }, [companyName]);

  if (loading) {
    return <div>Loading stock details...</div>;
  }

  if (error) {
    return <div>Error fetching stock details: {error}</div>;
  }

  // Construct the Power BI URL using the company name
  const powerBiUrl = `https://app.powerbi.com/reportEmbed?reportId=de902472-a03e-432c-93a8-74ea3700a1f4&autoAuth=true&ctid=813e6569-4e44-4d95-88a0-16a97bd5277c&filter=Company/Name eq '${encodeURIComponent(stockDetails.company_name)}'`;

  return (
    <div>
      <Navbar />
      {stockDetails ? (
        <div className="stock-details-container">
          <h1 className="company-name">{stockDetails.company_name || 'Company Name'}</h1>

          <div className="stock-details-grid">
            {/* Other sections omitted for brevity */}

            {/* Power BI Report for Stock Performance */}
            <div className="card powerbi-visualization">
              <h3>Stock Performance Visualization:</h3>
              <iframe
                title="stock"
                width="1140"
                height="541.25"
                src={powerBiUrl} // Use the correctly defined variable
                frameBorder="0"
                allowFullScreen={true}
              ></iframe>
            </div>
          </div>
        </div>
      ) : (
        <div>No stock details found.</div>
      )}
    </div>
  );
};

export default StockDetails;
