import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../Navbar';
import '../components/StockDetails.css'; // Assuming your CSS is here

const StockDetails = () => {
  const { companyName } = useParams();
  const [stockDetails, setStockDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/stocks/company/${encodeURIComponent(companyName)}`);
        
        if (!response.ok) {
          throw new Error(`Error fetching stock details: ${response.statusText}`);
        }

        const data = await response.json();
        setStockDetails(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching stock details:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchStockDetails();
  }, [companyName]);

  if (loading) {
    return <div>Loading stock details...</div>;
  }

  if (error) {
    return <div>Error fetching stock details: {error}</div>;
  }

  return (
    <div>
      <Navbar />
      {stockDetails ? (
        <div className="stock-details-container">
          <h1 className="company-name">{stockDetails.company_name || 'Company Name'}</h1>
          
          <div className="stock-details-grid">
            {/* Left Column: Price Summary and Buy/Sell Recommendations */}
            <div className="left-column">
              {/* Price Summary Section */}
              <div className="card price-summary">
                <h3>Price Summary</h3>
                <div className="detail-row">

</div>
<div className="detail-row">
   <span className="label">TODAY'S HIGH:</span>
  <span className="value">₹{stockDetails.today_high ?? 'N/A'}</span> 
  <span className="label">TODAY'S LOW:</span>
  <span className="value">₹{stockDetails.today_low ?? 'N/A'}</span>
</div>
<div className="detail-row">
  <span className="label">52 Week High:</span>
  <span className="value">₹{stockDetails.week_52_high ?? 'N/A'}</span>
</div>
<div className="detail-row">
  <span className="label">52 Week Low:</span>
  <span className="value">₹{stockDetails.week_52_low ?? 'N/A'}</span>
</div>
              </div>

              {/* Buy/Sell Recommendations Section */}
              <div className="card recommendations">
                <h3>Buy/Sell Recommendations</h3>
                <div className="detail-row">
                  <span>Current Price:</span>
                  <span>₹{stockDetails.current_price ? stockDetails.current_price.toFixed(2) : 'N/A'}</span>
                </div>
                <div className="detail-row">
                  <span>Mean Price Target:</span>
                  <span>₹{stockDetails.mean_price_target ? stockDetails.mean_price_target.toFixed(2) : 'N/A'}</span>
                </div>
                <div className="detail-row">
                  <span>High Price Target:</span>
                  <span>₹{stockDetails.high_price_target ? stockDetails.high_price_target.toFixed(2) : 'N/A'}</span>
                </div>
                <div className="detail-row">
                  <span>Low Price Target:</span>
                  <span>₹{stockDetails.low_price_target ? stockDetails.low_price_target.toFixed(2) : 'N/A'}</span>
                </div>
              </div>
            </div>

            {/* Right Column: Company Essentials */}
            <div className="card company-essentials">
              <h3>Company Essentials</h3>
              <div className="detail-row">
                <span>Market Cap:</span>
                <span>₹{stockDetails.market_cap ? stockDetails.market_cap.toFixed(2) : 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>No. of Shares:</span>
                <span>{stockDetails.number_of_shares ? stockDetails.number_of_shares.toFixed(2) : 'N/A'} Cr</span>
              </div>
              <div className="detail-row">
                <span>PE Ratio:</span>
                <span>{stockDetails.pe_ratio ? stockDetails.pe_ratio.toFixed(2) : 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>EPS:</span>
                <span>{stockDetails.eps ? stockDetails.eps.toFixed(2) : 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>Dividend Yield:</span>
                <span>{stockDetails.dividend_yield ? stockDetails.dividend_yield.toFixed(2) : 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>Revenue:</span>
                <span>{stockDetails.revenue ? stockDetails.revenue.toFixed(2) : 'N/A'}</span>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div>No stock details found.</div>
      )}
    </div>
  );
};

export default StockDetails;
