import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './Navbar';
import StockTable from './components/StockTable'; // Ensure this is your existing StockTable component
import './App.css'; // Your main CSS file

const App = () => {
  const [stocks, setStocks] = useState([]);
  const [filteredStocks, setFilteredStocks] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then((response) => response.json())
      .then((data) => {
        setStocks(data);
        setFilteredStocks(data); // Initialize filtered stocks
      })
      .catch((error) => console.error('Error fetching stock data:', error));
  }, []);

  const handleSearch = (searchTerm) => {
    const filtered = searchTerm
      ? stocks.filter(stock => 
          stock.company && stock.company.toLowerCase().includes(searchTerm)
        )
      : stocks; // Show all stocks if the search term is empty

    setFilteredStocks(filtered);
  };

  return (
    <Router>
      <Navbar onSearch={handleSearch} />
      <Switch>
        <Route path="/stocks">
          <StockTable stocks={filteredStocks} /> {/* Pass filtered stocks to StockTable */}
        </Route>
        <Route path="/">
          {/* Your Home component here */}
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
