import React, { useEffect, useState } from 'react';
import './StockTable.css';

const StockTable = () => {
  const [stocks, setStocks] = useState([]);
  const [filteredStocks, setFilteredStocks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then((response) => response.json())
      .then((data) => {
        console.log('Fetched Stock Data:', data);  // Log to check data structure
        setStocks(data);
        setFilteredStocks(data);  // Initialize filtered stocks
      })
      .catch((error) => console.error('Error fetching stock data:', error));
  }, []);

  const handleSearch = (e) => {
    const searchTerm = e.target.value.toLowerCase();
    setSearchTerm(searchTerm);
    
    const filtered = stocks.filter((stock) => {
      // Safeguard against undefined company field
      return stock.company && stock.company.toLowerCase().includes(searchTerm);
    });

    setFilteredStocks(filtered);
  };

  return (
    <div className="stock-table-container">
      <div className="filter-container">
        <label htmlFor="search">Filter by company: </label>
        <input
          type="text"
          id="search"
          value={searchTerm}
          onChange={handleSearch}
          placeholder="Search for company..."
        />
      </div>
      <table className="stock-table">
        <thead>
          <tr>
            <th>COMPANY</th>
            <th>YESTERDAY OPENING</th>
            <th>YESTERDAY CLOSING</th>
            <th>TODAY HIGH</th>
            <th>TODAY LOW</th>
            <th>CURRENT VALUE</th>
          </tr>
        </thead>
        <tbody>
          {filteredStocks.map((stock, index) => (
            <tr key={index}>
              <td>{stock.company || 'N/A'}</td> {/* Default to 'N/A' if company is undefined */}
              <td>{stock.yesterday_opening || 'N/A'}</td>
              <td>{stock.yesterday_closing || 'N/A'}</td>
              <td>{stock.today_high || 'N/A'}</td>
              <td>{stock.today_low || 'N/A'}</td>
              <td>{stock.current_value || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockTable;
