import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../Navbar';
import '../components/StockDetails.css';

const StockDetails = () => {
  const { companyName } = useParams();
  const [stockDetails, setStockDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/stocks/company/${encodeURIComponent(companyName)}`);
        
        if (!response.ok) {
          throw new Error(`Error fetching stock details: ${response.statusText}`);
        }

        const data = await response.json();
        
        console.log('Fetched stock details:', data);
        
        if (data && data.company_name) {
          setStockDetails(data);
        } else {
          throw new Error('No valid stock details found');
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching stock details:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchStockDetails();
  }, [companyName]);

  if (loading) {
    return <div>Loading stock details...</div>;
  }

  if (error) {
    return <div>Error fetching stock details: {error}</div>;
  }

  return (
    <div>
      <Navbar />
      {stockDetails ? (
        <div className="stock-details-container">
          <h1 className="company-name">{stockDetails.company_name || 'Company Name'}</h1>
          <div className="summary-container">
            {/* Price Summary Section */}
            <div className="box price-summary">
              <h3>Price Summary</h3>
              <div className="detail-row">
                <span>TODAY'S HIGH:</span>
                <span>₹{stockDetails.today_high ?? 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>TODAY'S LOW:</span>
                <span>₹{stockDetails.today_low ?? 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>52 Week High:</span>
                <span>₹{stockDetails.week_52_high ?? 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>52 Week LOW:</span>
                <span>₹{stockDetails.week_52_low ?? 'N/A'}</span>
              </div>
            </div>
            {/* Buy/Sell Recommendations Section */}
            <div className="box recommendations">
              <h3>Buy/Sell Recommendations</h3>
              <div className="detail-row">
                <span>Current Price:</span>
                <span>₹{stockDetails.current_price ?? 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>Mean Price Target:</span>
                <span>₹{stockDetails.mean_price_target ?? 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>High Price Target:</span>
                <span>₹{stockDetails.high_price_target ?? 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>Low Price Target:</span>
                <span>₹{stockDetails.low_price_target ?? 'N/A'}</span>
              </div>
            </div>
            {/* Company Essentials Section */}
            <div className="box company-essentials">
              <h3>Company Essentials</h3>
              <div className="detail-row">
                <span>Market Cap:</span>
                <span>₹{stockDetails.market_cap ?? 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>No. of Shares:</span>
                <span>{stockDetails.number_of_shares ?? 'N/A'} Cr</span>
              </div>
              <div className="detail-row">
                <span>Equaity Per Share:</span>
                <span>{stockDetails.eps ?? 'N/A'}</span>
              </div>
              <div className="detail-row">
                <span>Revenue:</span>
                <span>{stockDetails.revenue ?? 'N/A'}%</span>
              </div>
              <div className="detail-row">
                <span>Dividend Yield :</span>
                <span>{stockDetails.dividend_yield ?? 'N/A'}%</span>
              </div>
              <div className="detail-row">
                <span>Pe Ratio:</span>
                <span>{stockDetails.pe_ratio ?? 'N/A'}%</span>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div>No stock details found.</div>
      )}
    </div>
  );
};

export default StockDetails;
