import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const StockDetails = () => {
  const { companyName } = useParams(); // Get the company name from URL parameters
  const [stockDetails, setStockDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockDetails = async () => {
      try {
        // Use backticks and encodeURIComponent to form the URL
        const response = await fetch(`http://localhost:5000/api/stocks/company/${encodeURIComponent(companyName)}`);
        
        if (!response.ok) {
          throw new Error(`Error fetching stock details: ${response.statusText}`);
        }

        const data = await response.json();
        setStockDetails(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching stock details:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchStockDetails();
  }, [companyName]);

  if (loading) {
    return <div>Loading stock details...</div>;
  }

  if (error) {
    return <div>Error fetching stock details: {error}</div>;
  }

  return (
    <div>
      {stockDetails ? (
        <div>
          <h1>{stockDetails.company_name}</h1>
          <p>Current Price: {stockDetails.current_price}</p>
          <p>Today High: {stockDetails.today_high}</p>
          <p>Today Low: {stockDetails.today_low}</p>
          <p>Yesterday Opening: {stockDetails.yesterday_open}</p>
          <p>Yesterday Closing: {stockDetails.yesterday_close}</p>
          {/* Add more details as needed */}
        </div>
      ) : (
        <div>No stock details found.</div>
      )}
    </div>
  );
};

export default StockDetails;
