import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StockTable.css';

const StockTable = () => {
  const [stocks, setStocks] = useState([]);
  const [filteredStocks, setFilteredStocks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();  // React Router hook

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then((response) => response.json())
      .then((data) => {
        console.log('Fetched Stock Data:', data);  // Log to check data structure
        setStocks(data);
        setFilteredStocks(data);  // Initialize filtered stocks
      })
      .catch((error) => console.error('Error fetching stock data:', error));
  }, []);

  const handleSearch = (e) => {
    const searchTerm = e.target.value.toLowerCase();
    setSearchTerm(searchTerm);
    
    const filtered = searchTerm
      ? stocks.filter((stock) => 
          stock.company && stock.company.toLowerCase().includes(searchTerm)
        )
      : stocks;  // If searchTerm is empty, show all stocks

    setFilteredStocks(filtered);
  };

  const handleStockClick = (stock) => {
    // Navigate to the stock details page using company name as a parameter
    navigate(`/stock-details/${encodeURIComponent(stock.company_name)}`);
  };

  return (
    <div className="stock-table-container">
      <div className="filter-bar">
        <label className="filter-text" htmlFor="search">Filter by company: </label>
        <div className="search-container">
          <input
            type="text"
            id="search"
            className="filter-input"
            value={searchTerm}
            onChange={handleSearch}
            placeholder="Search for company..."
          />
        </div>
      </div>
      <div className="table-scroll-container">
        <table className="stock-table">
          <thead>
            <tr>
              <th>COMPANY</th>
              <th>YESTERDAY OPENING</th>
              <th>YESTERDAY CLOSING</th>
              <th>TODAY HIGH</th>
              <th>TODAY LOW</th>
              <th>CURRENT VALUE</th>
            </tr>
          </thead>
          <tbody>
            {filteredStocks.map((stock, index) => (
              <tr key={index} onClick={() => handleStockClick(stock)} style={{ cursor: 'pointer' }}>
                <td>{stock.company_name || 'N/A'}</td>
                <td>{stock.yesterday_open || 'N/A'}</td>
                <td>{stock.yesterday_close || 'N/A'}</td>
                <td>{stock.today_high || 'N/A'}</td>
                <td>{stock.today_low || 'N/A'}</td>
                <td>{stock.current_price || 'N/A'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default StockTable;
