import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const StockDetails = () => {
  const { ticker } = useParams(); // Get the stock ticker from the URL
  const [stockDetails, setStockDetails] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/stocks/${ticker}`)  // Fetch details based on ticker
      .then((response) => response.json())
      .then((data) => setStockDetails(data))
      .catch((error) => console.error('Error fetching stock details:', error));
  }, [ticker]);

  if (!stockDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{stockDetails.company_name}</h1>
      <div className="stock-detail-container">
        <div className="detail-card">
          <h3>Price Summary</h3>
          <p>Today's High: ₹{stockDetails.today_high}</p>
          <p>Today's Low: ₹{stockDetails.today_low}</p>
          <p>52 Week High: ₹{stockDetails.year_high}</p>
          <p>52 Week Low: ₹{stockDetails.year_low}</p>
        </div>
        <div className="detail-card">
          <h3>Company Essentials</h3>
          <p>Market Cap: ₹{stockDetails.market_cap}</p>
          <p>No. of Shares: {stockDetails.no_of_shares} Cr</p>
          <p>Sales Growth: {stockDetails.sales_growth}%</p>
          <p>ROE: {stockDetails.roe}%</p>
          <p>ROCE: {stockDetails.roce}%</p>
          <p>Profit Growth: {stockDetails.profit_growth}%</p>
        </div>
        <div className="detail-card">
          <h3>Buy/Sell Recommendations</h3>
          <p>Current Price: ₹{stockDetails.current_price}</p>
          <p>Mean Price Target: ₹{stockDetails.mean_price_target}</p>
          <p>High Price Target: ₹{stockDetails.high_price_target}</p>
          <p>Low Price Target: ₹{stockDetails.low_price_target}</p>
        </div>
      </div>
    </div>
  );
};

export default StockDetails;
