import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const StockDetails = () => {
  const { ticker } = useParams(); // Get the ticker from the URL parameters
  const [stockData, setStockData] = useState(null); // To hold stock data
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    // Fetch stock data from your backend (GCP or any API endpoint)
    fetch(`http://localhost:5000/api/stocks/${ticker}`)
      .then((response) => response.json())
      .then((data) => {
        setStockData(data); // Set the stock data
        setLoading(false); // Stop loading once data is fetched
      })
      .catch((error) => {
        console.error('Error fetching stock details:', error);
        setError('Error fetching stock details');
        setLoading(false); // Stop loading even if there's an error
      });
  }, [ticker]);

  if (loading) {
    return <div>Loading...</div>; // Display a loading message while fetching
  }

  if (error) {
    return <div>{error}</div>; // Display an error message if fetching fails
  }

  if (!stockData) {
    return <div>No stock data available.</div>; // Display if no stock data is available
  }

  return (
    <div className="stock-details-container">
      <h2>{stockData.company_name}</h2>
      <div className="stock-info">
        <div className="price-summary">
          <h3>Price Summary</h3>
          <p>Today's High: ₹{stockData.today_high}</p>
          <p>Today's Low: ₹{stockData.today_low}</p>
          <p>52 Week High: ₹{stockData.high_52_week}</p>
          <p>52 Week Low: ₹{stockData.low_52_week}</p>
        </div>
        <div className="company-essentials">
          <h3>Company Essentials</h3>
          <p>Market Cap: ₹{stockData.market_cap}</p>
          <p>No. of Shares: {stockData.no_of_shares}</p>
          <p>Sales Growth: {stockData.sales_growth}%</p>
          <p>ROE: {stockData.roe}%</p>
          <p>ROCE: {stockData.roce}%</p>
          <p>Profit Growth: {stockData.profit_growth}%</p>
        </div>
      </div>
    </div>
  );
};

export default StockDetails;
