import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../Navbar';
import * as powerbi from "powerbi-client"; // Import Power BI client
import '../components/StockDetails.css';

const StockDetails = () => {
  const { companyName } = useParams(); // Fetch the company name from the URL
  const [stockDetails, setStockDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch stock details
  useEffect(() => {
    const fetchStockDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/stocks/company/${encodeURIComponent(companyName)}`);
        
        if (!response.ok) {
          throw new Error(`Error fetching stock details: ${response.statusText}`);
        }

        const data = await response.json();
        setStockDetails(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching stock details:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchStockDetails();
  }, [companyName]);

  // Embed Power BI report and filter by company name
  useEffect(() => {
    if (stockDetails) {
      const embedConfig = {
        type: 'report',
        embedUrl: "<YOUR_EMBED_URL>", // Replace with your Power BI embed URL
        tokenType: powerbi.models.TokenType.Embed,
        accessToken: "<YOUR_ACCESS_TOKEN>", // Replace with valid Power BI access token
        settings: {
          filterPaneEnabled: false,
          navContentPaneEnabled: false
        },
        filters: [
          {
            $schema: "http://powerbi.com/product/schema#basic",
            target: {
              table: "Company", // Replace with actual table name in your Power BI dataset
              column: "CompanyName" // Replace with the column name for company names
            },
            operator: "In",
            values: [stockDetails.company_name] // Apply filter using the company name
          }
        ]
      };

      const reportContainer = document.getElementById('powerbi-report-container');
      const powerbiService = new powerbi.service.Service(powerbi.factories.hpmFactory, powerbi.factories.wpmpFactory, powerbi.factories.routerFactory);
      const report = powerbiService.embed(reportContainer, embedConfig);

      report.on("loaded", function() {
        console.log("Power BI report loaded successfully.");
      });

      report.on("error", function(event) {
        console.error(event.detail);
      });
    }
  }, [stockDetails]);

  if (loading) {
    return <div>Loading stock details...</div>;
  }

  if (error) {
    return <div>Error fetching stock details: {error}</div>;
  }

  return (
    <div>
      <Navbar />
      {stockDetails ? (
        <div className="stock-details-container">
          <h1 className="company-name">{stockDetails.company_name || 'Company Name'}</h1>

          <div className="stock-details-grid">
            {/* Left Column: Stock Details */}
            <div className="left-column">
              <div className="card price-summary">
                <h3>Price Summary</h3>
                {/* Your existing stock details here */}
              </div>

              <div className="card recommendations">
                <h3>Buy/Sell Recommendations</h3>
                {/* Your existing stock recommendations */}
              </div>
            </div>

            {/* Right Column: Power BI Report */}
            <div className="card powerbi-report">
              <h3>Stock Analysis Visualization</h3>
              <div id="powerbi-report-container" style={{ height: '500px', width: '100%' }}>
                {/* Power BI report will be embedded here */}
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div>No stock details found.</div>
      )}
    </div>
  );
};

export default StockDetails;
