import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../Navbar';
import '../components/StockDetails.css';

const StockDetails = () => {
  const { companyName } = useParams();
  const [stockDetails, setStockDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/stocks/company/${encodeURIComponent(companyName)}`);
        if (!response.ok) {
          throw new Error(`Error fetching stock details: ${response.statusText}`);
        }
        const data = await response.json();
        if (data && data.company_name) {
          setStockDetails(data);
        } else {
          throw new Error('No valid stock details found');
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching stock details:', error);
        setError(error.message);
        setLoading(false);
      }
    };
    fetchStockDetails();
  }, [companyName]);

  if (loading) {
    return <div>Loading stock details...</div>;
  }

  if (error) {
    return <div>Error fetching stock details: {error}</div>;
  }

  const formatNumber = (num) => {
    return num ? num.toFixed(2) : 'N/A';
  };

  return (
    <div>
      <Navbar />
      {stockDetails ? (
        <div className="stock-details-container">
          <h1 className="company-name">{stockDetails.company_name || 'Company Name'}</h1>
          <div className="summary-container">
            
            {/* Price Summary Section */}
            <div className="price-summary box">
              <h3>Price Summary</h3>
              <div className="detail-row">
                <span>TODAY'S HIGH:</span>
                <span>₹{formatNumber(stockDetails.today_high)}</span>
              </div>
              <div className="detail-row">
                <span>TODAY'S LOW:</span>
                <span>₹{formatNumber(stockDetails.today_low)}</span>
              </div>
              <div className="detail-row">
                <span>52 Week High:</span>
                <span>₹{formatNumber(stockDetails.week_52_high)}</span>
              </div>
              <div className="detail-row">
                <span>52 Week LOW:</span>
                <span>₹{formatNumber(stockDetails.week_52_low)}</span>
              </div>
            </div>

            {/* Buy/Sell Recommendations Section */}
            <div className="recommendations box">
              <h3>Buy/Sell Recommendations</h3>
              <div className="detail-row">
                <span>Current Price:</span>
                <span>₹{formatNumber(stockDetails.current_price)}</span>
              </div>
              <div className="detail-row">
                <span>Mean Price Target:</span>
                <span>₹{formatNumber(stockDetails.mean_price_target)}</span>
              </div>
              <div className="detail-row">
                <span>High Price Target:</span>
                <span>₹{formatNumber(stockDetails.high_price_target)}</span>
              </div>
              <div className="detail-row">
                <span>Low Price Target:</span>
                <span>₹{formatNumber(stockDetails.low_price_target)}</span>
              </div>
            </div>

            {/* Company Essentials Section */}
            <div className="company-essentials box">
              <h3>Company Essentials</h3>
              <div className="detail-row">
                <span>Market Cap:</span>
                <span>₹{formatNumber(stockDetails.market_cap)}</span>
              </div>
              <div className="detail-row">
                <span>No. of Shares:</span>
                <span>{formatNumber(stockDetails.no_of_shares)} Cr</span>
              </div>
              <div className="detail-row">
                <span>Sales Growth:</span>
                <span>{formatNumber(stockDetails.sales_growth)}%</span>
              </div>
              <div className="detail-row">
                <span>ROE:</span>
                <span>{formatNumber(stockDetails.roe)}%</span>
              </div>
              <div className="detail-row">
                <span>ROCE:</span>
                <span>{formatNumber(stockDetails.roce)}%</span>
              </div>
              <div className="detail-row">
                <span>Profit Growth:</span>
                <span>{formatNumber(stockDetails.profit_growth)}%</span>
              </div>
            </div>

          </div>
        </div>
      ) : (
        <div>No stock details found.</div>
      )}
    </div>
  );
};

export default StockDetails;
