// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import Lottie from 'lottie-react';
import animationData from '../Animation.json'; // Correct path to animation JSON
import Navbar from '../Navbar'; // Import Navbar

const HomePage = () => {
  const navigate = useNavigate();

  const handleGetStarted = () => {
    navigate('/stock-page'); // Navigate to the stock page
  };

  return (
<div className="App">
      {/* Navigation Bar */}
      <nav className="navbar">
        <h2>Finance Portal</h2>
      </nav>

      {/* Main content */}
      <div className="main-content">
        <h1>
          All things finance,<br /> right here.
        </h1>

        {/* Lottie Animation */}
        <div className="lottie-container">
          <Lottie
            animationData={require('./Animation.json')}  // Load JSON from src folder
            style={{ height: '300px', width: '500px' }}
            loop={true}  // Ensure looping is enabled
            autoplay={true}  // Ensure autoplay is enabled
          />
        </div>

        {/* Get Started Button */}
        <button className="get-started-button">Get Started</button>
      </div>
    </div>
  );
};

export default HomePage;
