// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import Lottie from 'lottie-react';
import animationData from '../Animation.json'; // Correct path to animation JSON
import Navbar from '../Navbar'; // Import Navbar component

const HomePage = () => {
  const navigate = useNavigate();

  const handleGetStarted = () => {
    navigate('/stock-page'); // Navigate to the stock page
  };

  return (
    <div className="home-page">
      {/* Full-width Navbar */}
      <Navbar />

      {/* Main Content */}
      <div className="content">
        <h1 className="main-heading">All things finance,</h1>
        <h1 className="sub-heading">right here.</h1>

        {/* Animation */}
        <div className="animation-container">
          <Lottie animationData={animationData} loop={true} style={{ width: '400px', height: '400px' }} />
        </div>

        {/* Get Started Button */}
        <button onClick={handleGetStarted} className="get-started-btn">
          Get Started
        </button>
      </div>
    </div>
  );
};

export default HomePage;
